<span class="line">    <span class="token keyword">def</span> <span class="token function">_mark_completed</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> coro_id<span class="token punctuation">:</span> <span class="token builtin">str</span><span class="token punctuation">,</span> status<span class="token punctuation">:</span> <span class="token builtin">str</span><span class="token punctuation">,</span> result<span class="token punctuation">:</span> Any<span class="token punctuation">,</span> error<span class="token punctuation">:</span> Exception <span class="token operator">=</span> <span class="token boolean">None</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
